// Add item to token
for (let t of canvas.tokens.controlled) {
    let actor = t.actor;
    if (actor) {
       let itemName = "New Item";
       const itemData = {
         "name": itemName,
         "img": "",
         "type": "item"
       }
       Item.create(itemData, { parent: actor });
    }
}
